import React,{useState,useContext} from "react";
import { UserContext } from "../../App";
import M from 'materialize-css';
import {useNavigate,Link} from 'react-router-dom'
import axios from 'axios';

function Signin (){
    const {state,dispatch} = useContext(UserContext)
    const navigate = useNavigate();
    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");
    const [user, setUser] = useState()

    const handleSubmit = async e => {
        e.preventDefault();
        const user = { username, password };
        // send the username and password to the server
        const response = await axios.post(
            "http://blogservice.herokuapp.com/api/login",
            user
        );
        // set the state of the user
        setUser(response.data)
        // store the user in localStorage
        localStorage.setItem('user', response.data)
        console.log(response.data)
    };
    useEffect(() => {
        const loggedInUser = localStorage.getItem("user");
        if (loggedInUser) {
          const foundUser = JSON.parse(loggedInUser);
          setUser(foundUser);
        }
      }, []);

    

    const Postdata =()=>{
        
        fetch("/signin",{
            method:"post",
            headers:{
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                email,
                password
            })

            }).then(res=>res.json())
            .then(data=>{
                console.log(data)
                if (data.error){
                    M.toast({html: data.error,classes:"#e53935 red darken-1"})
                }
                else if(!/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email)){
                    return M.toast({html: "invalid email",classes:"#e53935 red darken-1"})
                }
                else{
                    localStorage.setItem("jwt", data.token)
                    localStorage.setItem("user", JSON.stringify(data.user))
                    dispatch({type:"USER", payload:data.user})
                    M.toast({html: "Succesful login",classes:"#388e3c green darken-2"})
                    navigate("/home")
                }
            }).catch(err=>{
                console.log(err)
        })
    }



    return(
        <form className="Sign" ons>
        <div className="card center">
            <h3>LOG-IN</h3>
            <input type="email" placeholder="email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
            <input type="password" placeholder="password" value={password} onChange={(e)=>setPassword(e.target.value)}/>
            <button class="btn waves-effect waves-light" type="submit" name="action" onClick={()=>Postdata()}>Submit</button>
        </div >
        <h5 style={{textAlign:"center"}}>Not a user? <Link to="/signup">Sign Up</Link></h5>
        </form>       
    );
}

export default Signin;
