        }
    }
    const renderList2=()=>{
        if(state){
            return[
                <li onClick={handleLink}><Link to="/" >Sign Out</Link></li>,
                <li><Link to="/profile"><i class="large material-icons">account_circle</i></Link></li>
                

            ]
        }
        else{
            return[
                <li><Link to="/signin" className="signin" >Sign In</Link></li>,
                <li><Link to="/signup">Sign Up</Link></li>
            ]
        }
    }

    return (
        <nav>
            <div className="nav-wrapper">
                {renderList1()}
                <Link to="/" className="brand-logo">mYeat</Link>
                <ul id="nav-mobile" className="right">
                    {renderList2()}
                </ul>
            </div>
        </nav>
    );
}
export default Navbar;
