import React,{useState,useEffect} from "react";
import Comments from "./Comments";
function Postcard (){

    const [data,setData] = useState([]);
    useEffect(()=>{
        fetch('/allpost',{
            headers: {
                "Authorization": "Bearer " +localStorage.getItem("jwt")
            }

        }).then(res=>res.json())
        .then(result=>{
            setData(result.posts)
        })
    },[])

    return(
        <div class="card pic">
            <h6 style={{marginLeft:"10px"}}>username</h6>
            <div class="card-image" style={{marginLeft:"10px",marginRight:"10px"}}>
                <img src="https://th.bing.com/th/id/OIP.fVteXNVbJejGLybVXCmQBwHaE8?pid=ImgDet&rs=1"/> 
            </div>
            
            <div class="card-content" style={{textAlign:"left"}}>
                <span class="card-title" style={{color:"#2568FB"}}>Card Title</span>
                <p>I am a very simple card. I am good at containing small bits of information.
                    I am convenient because I require little markup to use effectively.
                </p>
                
            </div>
            <div style={{display:"flex",flexDirection:"space-between",margin:"10px 20px"}}>
                <i class="material-icons" style={{fontSize:"1.5rem",marginTop:"9px",marginRight:"4px",color:"#FECD45"}}>favorite_border</i>
                <h6 > 253 Likes</h6>
            </div>
            <div class="card-action">
                <Comments />
            </div>
        </div>    
    );
}

export default Postcard;
