import React,{useState,useEffect} from "react";
import Createpost from "./Createpost";
import Postcard from "./Postcard";
function Home (){

    const [data,setData] = useState([]);
    useEffect(()=>{
        fetch('/allpost',{
            headers: {
                "Authorization": "Bearer " +localStorage.getItem("jwt")
            }

        }).then(res=>{res.json()})
        .then(result=>{
            console.log(data)
            setData(result )
        })
    })
    return(
        <div className="Home">
            <Createpost />
            <div className="Postcard">
                {
                data.map(item=>{
                    return( <Postcard />)
                    })
                }
                
            </div>
        </div>       
    );
}

export default Home;
